import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { apiClient } from '../config/api';

const TimesheetManagement = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [timesheets, setTimesheets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingTimesheet, setEditingTimesheet] = useState(null);
  const [formData, setFormData] = useState({
    payPeriodStart: '',
    payPeriodEnd: '',
    regularHours: '',
    overtimeHours: '',
    department: '',
    location: '',
    comments: ''
  });

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }
    fetchTimesheets();
  }, [user, navigate]);

  const fetchTimesheets = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await apiClient.get('/timesheets/my');
      setTimesheets(response.data);
    } catch (err) {
      console.error('Error fetching timesheets:', err);
      setError('Failed to fetch timesheets');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validation
    if (!formData.payPeriodStart || !formData.payPeriodEnd) {
      setError('Please enter both start and end dates');
      return;
    }

    if (!formData.regularHours || parseFloat(formData.regularHours) <= 0) {
      setError('Please enter regular hours (must be greater than 0)');
      return;
    }

    if (!formData.department || !formData.location) {
      setError('Please enter department and location');
      return;
    }

    try {
      const timesheetData = {
        employeeId: user?.userId || user?.username,
        employeeName: user?.username || 'Unknown',
        payPeriodStart: formData.payPeriodStart,
        payPeriodEnd: formData.payPeriodEnd,
        regularHours: parseFloat(formData.regularHours) || 0,
        overtimeHours: parseFloat(formData.overtimeHours) || 0,
        holidayHours: 0,
        sickHours: 0,
        vacationHours: 0,
        department: formData.department,
        location: formData.location,
        comments: formData.comments || ''
      };

      if (editingTimesheet) {
        await apiClient.put(`/timesheets/${editingTimesheet.id}`, timesheetData);
        setSuccess('Timesheet updated successfully!');
      } else {
        await apiClient.post('/timesheets', timesheetData);
        setSuccess('Timesheet created successfully!');
      }

      setShowForm(false);
      setEditingTimesheet(null);
      setFormData({
        payPeriodStart: '',
        payPeriodEnd: '',
        regularHours: '',
        overtimeHours: '',
        department: '',
        location: '',
        comments: ''
      });
      fetchTimesheets();
    } catch (err) {
      console.error('Error saving timesheet:', err);

      // Better error handling
      if (err.response?.status === 400) {
        if (err.response?.data?.message) {
          setError(err.response.data.message);
        } else if (typeof err.response?.data === 'string' && err.response.data.includes('already exists')) {
          setError('A timesheet already exists for this pay period. Please choose different dates or edit the existing timesheet.');
        } else {
          setError('Invalid data. Please check all fields and try again.');
        }
      } else if (err.response?.status === 403) {
        setError('You do not have permission to create timesheets.');
      } else {
        setError('Failed to save timesheet. Please try again.');
      }
    }
  };

  const handleEdit = (timesheet) => {
    setEditingTimesheet(timesheet);
    setFormData({
      payPeriodStart: timesheet.payPeriodStart,
      payPeriodEnd: timesheet.payPeriodEnd,
      regularHours: timesheet.regularHours?.toString() || '',
      overtimeHours: timesheet.overtimeHours?.toString() || '',
      department: timesheet.department || '',
      location: timesheet.location || '',
      comments: timesheet.comments || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this timesheet?')) {
      try {
        await apiClient.delete(`/timesheets/${id}`);
        setSuccess('Timesheet deleted successfully!');
        fetchTimesheets();
      } catch (err) {
        console.error('Error deleting timesheet:', err);
        setError('Failed to delete timesheet');
      }
    }
  };

  const handleApprove = async (id) => {
    try {
      await apiClient.post(`/timesheets/${id}/approve`);
      setSuccess('Timesheet approved successfully!');
      fetchTimesheets();
    } catch (err) {
      console.error('Error approving timesheet:', err);
      setError(err.response?.data?.message || 'Failed to approve timesheet. You may not have permission.');
    }
  };

  const handleReject = async (id) => {
    const reason = prompt('Please enter rejection reason:');
    if (reason) {
      try {
        await apiClient.post(`/timesheets/${id}/reject`, { comments: reason });
        setSuccess('Timesheet rejected successfully!');
        fetchTimesheets();
      } catch (err) {
        console.error('Error rejecting timesheet:', err);
        setError(err.response?.data?.message || 'Failed to reject timesheet. You may not have permission.');
      }
    }
  };

  const getStatusBadge = (status) => {
    const badges = {
      DRAFT: 'badge-secondary',
      SUBMITTED: 'badge-primary',
      APPROVED: 'badge-success',
      REJECTED: 'badge-danger',
      REVISION_REQUESTED: 'badge-warning'
    };
    return badges[status] || 'badge-secondary';
  };

  return (
    <div className="min-h-screen bg-ca-secondary-50">
      {/* Header */}
      <div className="ca-header">
        <div className="container">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <div className="ca-logo">CA</div>
              <h1 className="text-xl font-bold text-ca-primary-900">Timesheet Management</h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-ca-primary-700">
                {user?.username} ({user?.role})
              </span>
              <button onClick={() => navigate('/')} className="btn btn-outline">
                Back to Dashboard
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container py-8">
        {/* Alerts */}
        {error && (
          <div className="alert alert-error mb-4">
            {error}
          </div>
        )}
        {success && (
          <div className="alert alert-success mb-4">
            {success}
          </div>
        )}

        {/* Action Buttons */}
        <div className="mb-6 flex justify-between items-center">
          <h2 className="text-2xl font-bold text-ca-primary-900">My Timesheets</h2>
          {user?.role === 'PROVIDER' && (
            <button
              onClick={() => {
                if (showForm) {
                  setShowForm(false);
                  setEditingTimesheet(null);
                } else {
                  setShowForm(true);
                  setEditingTimesheet(null);
                  setError('');
                  setSuccess('');
                }
              }}
              className="btn btn-primary"
            >
              {showForm ? 'Cancel' : '+ New Timesheet'}
            </button>
          )}
        </div>

        {/* Timesheet Form */}
        {showForm && (
          <div className="card mb-6">
            <div className="card-header">
              <h3 className="text-lg font-semibold text-ca-primary-900">
                {editingTimesheet ? 'Edit Timesheet' : 'Create New Timesheet'}
              </h3>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="form-group">
                    <label className="form-label">Pay Period Start Date *</label>
                    <input
                      type="date"
                      required
                      className="input"
                      value={formData.payPeriodStart}
                      onChange={(e) => setFormData({...formData, payPeriodStart: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Pay Period End Date *</label>
                    <input
                      type="date"
                      required
                      className="input"
                      value={formData.payPeriodEnd}
                      onChange={(e) => setFormData({...formData, payPeriodEnd: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Regular Hours</label>
                    <input
                      type="number"
                      min="0"
                      step="0.5"
                      className="input"
                      placeholder="e.g., 40"
                      value={formData.regularHours}
                      onChange={(e) => setFormData({...formData, regularHours: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Overtime Hours</label>
                    <input
                      type="number"
                      min="0"
                      step="0.5"
                      className="input"
                      placeholder="e.g., 5"
                      value={formData.overtimeHours}
                      onChange={(e) => setFormData({...formData, overtimeHours: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Department *</label>
                    <input
                      type="text"
                      required
                      className="input"
                      placeholder="e.g., IT"
                      value={formData.department}
                      onChange={(e) => setFormData({...formData, department: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Location *</label>
                    <input
                      type="text"
                      required
                      className="input"
                      placeholder="e.g., Sacramento"
                      value={formData.location}
                      onChange={(e) => setFormData({...formData, location: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Comments</label>
                    <textarea
                      className="input"
                      rows="3"
                      placeholder="Additional notes..."
                      value={formData.comments}
                      onChange={(e) => setFormData({...formData, comments: e.target.value})}
                    />
                  </div>
                </div>
                <div className="flex justify-end space-x-2">
                  <button
                    type="button"
                    onClick={() => {
                      setShowForm(false);
                      setEditingTimesheet(null);
                      setFormData({
                        startDate: '',
                        endDate: '',
                        hoursWorked: '',
                        department: '',
                        location: '',
                        comments: ''
                      });
                    }}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editingTimesheet ? 'Update' : 'Create'} Timesheet
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Timesheets List */}
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-semibold text-ca-primary-900">Timesheets</h3>
          </div>
          <div className="card-body">
            {loading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-ca-highlight-600 mx-auto"></div>
                <p className="mt-4 text-ca-primary-600">Loading timesheets...</p>
              </div>
            ) : timesheets.length === 0 ? (
              <div className="text-center py-8 text-ca-primary-600">
                No timesheets found. Create your first timesheet!
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Pay Period Start</th>
                      <th>Pay Period End</th>
                      <th>Regular Hours</th>
                      <th>Overtime Hours</th>
                      <th>Total Hours</th>
                      <th>Department</th>
                      <th>Location</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {timesheets.map((timesheet) => (
                      <tr key={timesheet.id}>
                        <td>{timesheet.payPeriodStart}</td>
                        <td>{timesheet.payPeriodEnd}</td>
                        <td>{timesheet.regularHours || 0}</td>
                        <td>{timesheet.overtimeHours || 0}</td>
                        <td>{timesheet.totalHours || 0}</td>
                        <td>{timesheet.department || 'N/A'}</td>
                        <td>{timesheet.location || 'N/A'}</td>
                        <td>
                          <span className={`badge ${getStatusBadge(timesheet.status)}`}>
                            {timesheet.status}
                          </span>
                        </td>
                        <td>
                          <div className="flex space-x-2">
                            {user?.role === 'PROVIDER' && timesheet.status === 'DRAFT' && (
                              <>
                                <button
                                  onClick={() => handleEdit(timesheet)}
                                  className="btn btn-sm btn-secondary"
                                >
                                  Edit
                                </button>
                                <button
                                  onClick={() => handleDelete(timesheet.id)}
                                  className="btn btn-sm btn-danger"
                                >
                                  Delete
                                </button>
                              </>
                            )}
                            {user?.role === 'RECIPIENT' && timesheet.status === 'SUBMITTED' && (
                              <button
                                onClick={() => handleApprove(timesheet.id)}
                                className="btn btn-sm btn-success"
                              >
                                Approve
                              </button>
                            )}
                            {user?.role === 'CASE_WORKER' && (
                              <>
                                <button
                                  onClick={() => handleApprove(timesheet.id)}
                                  className="btn btn-sm btn-success"
                                >
                                  Approve
                                </button>
                                <button
                                  onClick={() => handleReject(timesheet.id)}
                                  className="btn btn-sm btn-danger"
                                >
                                  Reject
                                </button>
                              </>
                            )}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TimesheetManagement;
