      setPermissionForm({
        name: '',
        description: '',
        resourceId: '',
        scopeIds: [],
        policyIds: []
      });
      fetchPermissions();
    } catch (error) {
      showMessage('error', 'Failed to create permission: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  // Load data based on active tab
  useEffect(() => {
    switch (activeTab) {
      case 'users':
        fetchUsers();
        fetchRoles();
        break;
      case 'roles':
        fetchRoles();
        break;
      case 'policies':
        fetchPolicies();
        fetchRoles();
        break;
      case 'permissions':
        fetchPermissions();
        fetchResources();
        fetchPolicies();
        break;
      case 'resources':
        fetchResources();
        break;
      default:
        break;
    }
  }, [activeTab]);

  // ============================== RENDER ==============================

  // Show loading state while auth is initializing
  if (authLoading) {
    return (
      <div className="admin-container">
        <div style={{ textAlign: 'center', padding: '50px' }}>
          <div className="spinner"></div>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  // Check if user has ADMIN role
  if (!user || !user.roles || !user.roles.includes('ADMIN')) {
    return (
      <div className="admin-container">
        <div style={{ textAlign: 'center', padding: '50px' }}>
          <h1>ðŸ”’ Access Denied</h1>
          <p style={{ color: '#e74c3c', fontSize: '1.2rem', marginBottom: '20px' }}>
            Administrator role required to access this page.
          </p>
          <p>
            You need the <strong>ADMIN</strong> role to access the Keycloak Administration interface.
          </p>
          <div style={{ marginTop: '30px' }}>
            <p><strong>Current user:</strong> {user?.username || 'Not logged in'}</p>
            <p><strong>Current roles:</strong> {user?.roles?.join(', ') || 'None'}</p>
          </div>
        </div>
      </div>
