import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import './AdminDashboard.css';

const API_BASE_URL = 'http://localhost:8081/api/admin/keycloak';

/**
 * Keycloak Admin Dashboard
 * User-friendly interface for managing Keycloak without accessing the admin console
 */
const KeycloakAdminDashboard = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('users');
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [resources, setResources] = useState([]);
  const [policies, setPolicies] = useState([]);
  const [permissions, setPermissions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  // User form state
  const [userForm, setUserForm] = useState({
    username: '',
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    role: 'PROVIDER'
  });

  // Policy form state
  const [policyForm, setPolicyForm] = useState({
    name: '',
    description: '',
    roleName: 'PROVIDER'
  });

  // Permission form state
  const [permissionForm, setPermissionForm] = useState({
    name: '',
    description: '',
    resourceId: '',
    scopeIds: [],
    policyIds: []
  });

  const getAuthHeaders = () => {
    // Admin endpoints now require user authentication with ADMIN role
    const token = localStorage.getItem('token');
    return {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    };
  };

  const showMessage = (type, text) => {
    setMessage({ type, text });
    setTimeout(() => setMessage({ type: '', text: '' }), 5000);
  };

  // ============================== USER MANAGEMENT ==============================

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/users`, {
        headers: getAuthHeaders()
      });
      setUsers(response.data);
    } catch (error) {
      showMessage('error', 'Failed to fetch users: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const createUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/users`, userForm, {
        headers: getAuthHeaders()
      });
      showMessage('success', response.data.message || 'User created successfully');
      setUserForm({
        username: '',
        email: '',
        password: '',
        firstName: '',
        lastName: '',
        role: 'PROVIDER'
      });
      fetchUsers();
    } catch (error) {
      showMessage('error', 'Failed to create user: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const deleteUser = async (userId, username) => {
    if (!window.confirm(`Are you sure you want to delete user: ${username}?`)) {
      return;
    }

    setLoading(true);
    try {
      await axios.delete(`${API_BASE_URL}/users/${userId}`, {
        headers: getAuthHeaders()
      });
      showMessage('success', 'User deleted successfully');
      fetchUsers();
    } catch (error) {
      showMessage('error', 'Failed to delete user: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  // ============================== ROLE MANAGEMENT ==============================

  const fetchRoles = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/roles`, {
        headers: getAuthHeaders()
      });
      setRoles(response.data);
    } catch (error) {
      showMessage('error', 'Failed to fetch roles: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  // ============================== AUTHORIZATION MANAGEMENT ==============================

  const fetchResources = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/resources`, {
        headers: getAuthHeaders()
      });
      setResources(response.data);
    } catch (error) {
      showMessage('error', 'Failed to fetch resources: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const fetchPolicies = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/policies`, {
        headers: getAuthHeaders()
      });
      setPolicies(response.data);
    } catch (error) {
      showMessage('error', 'Failed to fetch policies: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const createPolicy = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/policies`, policyForm, {
        headers: getAuthHeaders()
      });
      showMessage('success', response.data.message || 'Policy created successfully');
      setPolicyForm({
        name: '',
        description: '',
        roleName: 'PROVIDER'
      });
      fetchPolicies();
    } catch (error) {
      showMessage('error', 'Failed to create policy: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const fetchPermissions = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/permissions`, {
        headers: getAuthHeaders()
      });
      setPermissions(response.data);
    } catch (error) {
      showMessage('error', 'Failed to fetch permissions: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const createPermission = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/permissions`, permissionForm, {
        headers: getAuthHeaders()
      });
      showMessage('success', response.data.message || 'Permission created successfully');
      setPermissionForm({
        name: '',
        description: '',
        resourceId: '',
        scopeIds: [],
        policyIds: []
      });
      fetchPermissions();
    } catch (error) {
      showMessage('error', 'Failed to create permission: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  // Load data based on active tab
  useEffect(() => {
    switch (activeTab) {
      case 'users':
        fetchUsers();
        fetchRoles();
        break;
      case 'policies':
        fetchPolicies();
        fetchRoles();
        break;
      case 'permissions':
        fetchPermissions();
        fetchResources();
        fetchPolicies();
        break;
      case 'resources':
        fetchResources();
        break;
      default:
        break;
    }
  }, [activeTab]);

  // ============================== RENDER ==============================

  return (
    <div className="admin-container">
      <h1 className="admin-title">🔐 Keycloak Administration</h1>
      <p className="admin-subtitle">Manage users, roles, policies, and permissions without accessing Keycloak console</p>

      {message.text && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="tabs">
        <button
          className={activeTab === 'users' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('users')}
        >
          👥 Users
        </button>
        <button
          className={activeTab === 'policies' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('policies')}
        >
          📋 Policies
        </button>
        <button
          className={activeTab === 'permissions' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('permissions')}
        >
          🔑 Permissions
        </button>
        <button
          className={activeTab === 'resources' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('resources')}
        >
          📦 Resources
        </button>
      </div>

      <div className="tab-content">
        {/* ============================== USERS TAB ============================== */}
        {activeTab === 'users' && (
          <div>
            <h2>Create New User</h2>
            <form onSubmit={createUser} className="admin-form">
              <div className="form-row">
                <div className="form-group">
                  <label>Username *</label>
                  <input
                    type="text"
                    value={userForm.username}
                    onChange={(e) => setUserForm({ ...userForm, username: e.target.value })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    value={userForm.email}
                    onChange={(e) => setUserForm({ ...userForm, email: e.target.value })}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>First Name</label>
                  <input
                    type="text"
                    value={userForm.firstName}
                    onChange={(e) => setUserForm({ ...userForm, firstName: e.target.value })}
                  />
                </div>
                <div className="form-group">
                  <label>Last Name</label>
                  <input
                    type="text"
                    value={userForm.lastName}
                    onChange={(e) => setUserForm({ ...userForm, lastName: e.target.value })}
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>Password *</label>
                  <input
                    type="password"
                    value={userForm.password}
                    onChange={(e) => setUserForm({ ...userForm, password: e.target.value })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Role</label>
                  <select
                    value={userForm.role}
                    onChange={(e) => setUserForm({ ...userForm, role: e.target.value })}
                  >
                    <option value="PROVIDER">Provider</option>
                    <option value="RECIPIENT">Recipient</option>
                    <option value="CASE_WORKER">Case Worker</option>
                  </select>
                </div>
              </div>
              <button type="submit" className="btn-primary" disabled={loading}>
                {loading ? 'Creating...' : 'Create User'}
              </button>
            </form>

            <h2>Existing Users</h2>
            {loading ? (
              <p>Loading users...</p>
            ) : (
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Username</th>
                    <th>Email</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Enabled</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.id}>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>{user.firstName || '-'}</td>
                      <td>{user.lastName || '-'}</td>
                      <td>{user.enabled ? '✅ Yes' : '❌ No'}</td>
                      <td>
                        <button
                          onClick={() => deleteUser(user.id, user.username)}
                          className="btn-danger-small"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {/* ============================== POLICIES TAB ============================== */}
        {activeTab === 'policies' && (
          <div>
            <h2>Create New Policy</h2>
            <form onSubmit={createPolicy} className="admin-form">
              <div className="form-group">
                <label>Policy Name *</label>
                <input
                  type="text"
                  value={policyForm.name}
                  onChange={(e) => setPolicyForm({ ...policyForm, name: e.target.value })}
                  placeholder="e.g., Recipient Read Policy"
                  required
                />
              </div>
              <div className="form-group">
                <label>Description</label>
                <input
                  type="text"
                  value={policyForm.description}
                  onChange={(e) => setPolicyForm({ ...policyForm, description: e.target.value })}
                  placeholder="e.g., Allows recipients to read timesheets"
                />
              </div>
              <div className="form-group">
                <label>Role *</label>
                <select
                  value={policyForm.roleName}
                  onChange={(e) => setPolicyForm({ ...policyForm, roleName: e.target.value })}
                >
                  {roles.filter(r => !r.name.startsWith('default-')).map((role) => (
                    <option key={role.id} value={role.name}>
                      {role.name}
                    </option>
                  ))}
                </select>
              </div>
              <button type="submit" className="btn-primary" disabled={loading}>
                {loading ? 'Creating...' : 'Create Policy'}
              </button>
            </form>

            <h2>Existing Policies</h2>
            {loading ? (
              <p>Loading policies...</p>
            ) : (
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Description</th>
                    <th>Logic</th>
                  </tr>
                </thead>
                <tbody>
                  {policies.map((policy) => (
                    <tr key={policy.id}>
                      <td>{policy.name}</td>
                      <td>{policy.type}</td>
                      <td>{policy.description || '-'}</td>
                      <td>{policy.logic}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {/* ============================== PERMISSIONS TAB ============================== */}
        {activeTab === 'permissions' && (
          <div>
            <h2>Create New Permission</h2>
            <form onSubmit={createPermission} className="admin-form">
              <div className="form-group">
                <label>Permission Name *</label>
                <input
                  type="text"
                  value={permissionForm.name}
                  onChange={(e) => setPermissionForm({ ...permissionForm, name: e.target.value })}
                  placeholder="e.g., Recipient Read Timesheets"
                  required
                />
              </div>
              <div className="form-group">
                <label>Description</label>
                <input
                  type="text"
                  value={permissionForm.description}
                  onChange={(e) => setPermissionForm({ ...permissionForm, description: e.target.value })}
                  placeholder="e.g., Allows recipients to read timesheet data"
                />
              </div>
              <div className="form-group">
                <label>Resource *</label>
                <select
                  value={permissionForm.resourceId}
                  onChange={(e) => setPermissionForm({ ...permissionForm, resourceId: e.target.value })}
                  required
                >
                  <option value="">Select a resource</option>
                  {resources.map((resource) => (
                    <option key={resource._id} value={resource._id}>
                      {resource.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Scopes * (comma-separated scope IDs)</label>
                <input
                  type="text"
                  value={permissionForm.scopeIds.join(',')}
                  onChange={(e) => setPermissionForm({ 
                    ...permissionForm, 
                    scopeIds: e.target.value.split(',').map(s => s.trim()).filter(s => s) 
                  })}
                  placeholder="e.g., scope-id-1, scope-id-2"
                  required
                />
              </div>
              <div className="form-group">
                <label>Policies * (comma-separated policy IDs)</label>
                <input
                  type="text"
                  value={permissionForm.policyIds.join(',')}
                  onChange={(e) => setPermissionForm({ 
                    ...permissionForm, 
                    policyIds: e.target.value.split(',').map(s => s.trim()).filter(s => s) 
                  })}
                  placeholder="e.g., policy-id-1, policy-id-2"
                  required
                />
              </div>
              <button type="submit" className="btn-primary" disabled={loading}>
                {loading ? 'Creating...' : 'Create Permission'}
              </button>
            </form>

            <h2>Existing Permissions</h2>
            {loading ? (
              <p>Loading permissions...</p>
            ) : (
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Description</th>
                    <th>Decision Strategy</th>
                  </tr>
                </thead>
                <tbody>
                  {permissions.map((permission) => (
                    <tr key={permission.id}>
                      <td>{permission.name}</td>
                      <td>{permission.type}</td>
                      <td>{permission.description || '-'}</td>
                      <td>{permission.decisionStrategy}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {/* ============================== RESOURCES TAB ============================== */}
        {activeTab === 'resources' && (
          <div>
            <h2>Resources</h2>
            {loading ? (
              <p>Loading resources...</p>
            ) : (
              <div>
                {resources.map((resource) => (
                  <div key={resource._id} className="resource-card">
                    <h3>{resource.name}</h3>
                    <p><strong>Type:</strong> {resource.type || '-'}</p>
                    <p><strong>URIs:</strong> {resource.uris?.join(', ') || '-'}</p>
                    {resource.scopes && resource.scopes.length > 0 && (
                      <div>
                        <strong>Scopes:</strong>
                        <ul>
                          {resource.scopes.map((scope) => (
                            <li key={scope.id}>{scope.name} (ID: {scope.id})</li>
                          ))}
                        </ul>
                      </div>
                    )}
                    {resource.attributes && Object.keys(resource.attributes).length > 0 && (
                      <div>
                        <strong>Attributes:</strong>
                        <ul>
                          {Object.entries(resource.attributes).map(([key, value]) => (
                            <li key={key}>{key}: {Array.isArray(value) ? value.join(', ') : value}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default KeycloakAdminDashboard;
