import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext(null);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check if JWT token is valid (not expired) - define before useEffect
  const isTokenValid = (token) => {
    try {
      const decodedToken = JSON.parse(atob(token.split('.')[1]));
      const currentTime = Math.floor(Date.now() / 1000);
      return decodedToken.exp > currentTime;
    } catch (error) {
      return false;
    }
  };

  useEffect(() => {
    const initializeAuth = async () => {
      // Check if user is already logged in
      const storedToken = localStorage.getItem('token');
      const storedUser = localStorage.getItem('user');

      if (storedToken && storedUser) {
        // Validate token before setting it
        if (isTokenValid(storedToken)) {
          setToken(storedToken);
          setUser(JSON.parse(storedUser));
        } else {
          // Token is expired, try to refresh it
          const refreshToken = localStorage.getItem('refreshToken');
          if (refreshToken) {
            try {
              const response = await axios.post(
                'http://localhost:8081/api/auth/refresh',
                { refresh_token: refreshToken },
                { headers: { 'Content-Type': 'application/json' } }
              );

              const { access_token, refresh_token: newRefreshToken } = response.data;
              localStorage.setItem('token', access_token);
              if (newRefreshToken) {
                localStorage.setItem('refreshToken', newRefreshToken);
              }
              setToken(access_token);
              setUser(JSON.parse(storedUser));
            } catch (error) {
              // Refresh failed, clear everything
              console.log('Token refresh failed on startup, logging out');
              localStorage.removeItem('token');
              localStorage.removeItem('refreshToken');
              localStorage.removeItem('user');
            }
          } else {
            // No refresh token, clear everything
            localStorage.removeItem('token');
            localStorage.removeItem('refreshToken');
            localStorage.removeItem('user');
          }
        }
      }
      setLoading(false);
    };

    initializeAuth();
  }, []);

  // Refresh token if it's expired
  const refreshTokenIfNeeded = async () => {
    const storedToken = localStorage.getItem('token');
    if (!storedToken) return null;

    if (!isTokenValid(storedToken)) {
      // Token is expired, try to refresh
      try {
        const refreshToken = localStorage.getItem('refreshToken');
        if (refreshToken) {
          const response = await axios.post(
            'http://localhost:8081/api/auth/refresh',
            { refresh_token: refreshToken },
            { headers: { 'Content-Type': 'application/json' } }
          );

          const { access_token } = response.data;
          localStorage.setItem('token', access_token);
          setToken(access_token);
          return access_token;
        }
      } catch (error) {
        // Refresh failed, logout user
        logout();
        return null;
      }
    }

    return storedToken;
  };

  const login = async (username, password) => {
    try {
      // Use backend login endpoint to avoid CORS issues
      const response = await axios.post(
        'http://localhost:8081/api/auth/login',
        {
          username: username,
          password: password,
        },
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      const { access_token, refresh_token } = response.data;

      // Decode JWT to get user info
      const decodedToken = JSON.parse(atob(access_token.split('.')[1]));
      const roles = decodedToken.realm_access?.roles || [];

      // Determine primary role
      let primaryRole = 'USER';
      if (roles.includes('PROVIDER')) primaryRole = 'PROVIDER';
      if (roles.includes('RECIPIENT')) primaryRole = 'RECIPIENT';
      if (roles.includes('CASE_WORKER')) primaryRole = 'CASE_WORKER';

      const userData = {
        username: decodedToken.preferred_username || username,
        roles: roles,
        role: primaryRole,
        userId: decodedToken.sub,
      };

      // Store tokens and user data
      localStorage.setItem('token', access_token);
      localStorage.setItem('refreshToken', refresh_token);
      localStorage.setItem('user', JSON.stringify(userData));

      setToken(access_token);
      setUser(userData);

      return true;
    } catch (error) {
      console.error('Login error:', error);
      return false;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('refreshToken');
    localStorage.removeItem('user');
    setToken(null);
    setUser(null);
  };

  const value = {
    user,
    token,
    login,
    logout,
    loading,
    refreshTokenIfNeeded,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
