import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { apiClient as api, API_ENDPOINTS } from '../config/api';
import NotificationCenter from './NotificationCenter';
import './WorkQueuePage.css';

const WorkQueuePage = () => {
  const { user, loading: authLoading, logout } = useAuth();
  const navigate = useNavigate();

  const [viewMode, setViewMode] = useState('queues');
  const [queues, setQueues] = useState([]);
  const [queueCounts, setQueueCounts] = useState({});
  const [selectedQueueId, setSelectedQueueId] = useState(null);
  const [selectedQueue, setSelectedQueue] = useState(null);
  const [queueTasks, setQueueTasks] = useState([]);
  const [queueSubscriptions, setQueueSubscriptions] = useState([]);
  const [userSubscriptions, setUserSubscriptions] = useState([]);
  const [loadingState, setLoadingState] = useState({ queues: false, tasks: false, inbox: false });
  const [queueTab, setQueueTab] = useState('tasks');
  const [inboxTasks, setInboxTasks] = useState([]);
  const [reservedTasks, setReservedTasks] = useState([]);
  const [taskCounts, setTaskCounts] = useState({ open: 0, reserved: 0, inProgress: 0, deferred: 0, closed: 0 });
  const [selectedTask, setSelectedTask] = useState(null);
  const [selectedTaskHistory, setSelectedTaskHistory] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(false);
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    if (authLoading) {
      return;
    }
    if (!user) {
      navigate('/login');
      return;
    }
    fetchQueues();
    fetchUserWorkspace();
  }, [authLoading, user, navigate, fetchQueues, fetchUserWorkspace]);

  useEffect(() => {
    if (selectedQueueId) {
      setQueueTab('tasks');
      fetchQueueDetail(selectedQueueId);
    }
  }, [selectedQueueId, fetchQueueDetail]);

  const isSubscribedToQueue = useMemo(() => {
    return userSubscriptions.some((subscription) => subscription.queueId === selectedQueueId);
  }, [userSubscriptions, selectedQueueId]);

  const fetchQueues = useCallback(async () => {
    if (!user) return;
    setLoadingState((prev) => ({ ...prev, queues: true }));
    try {
      const [queuesRes, countsRes, userSubsRes] = await Promise.all([
        api.get(API_ENDPOINTS.workQueues.list),
        api.get(API_ENDPOINTS.workQueues.counts),
        api.get(API_ENDPOINTS.workQueues.userSubscriptions(user.username)),
      ]);
      setQueues(queuesRes.data || []);
      setQueueCounts(countsRes.data || {});
      setUserSubscriptions(userSubsRes.data || []);
      if (!selectedQueueId && queuesRes.data?.length) {
        setSelectedQueueId(queuesRes.data[0].id);
      }
    } catch (error) {
      console.error('Error fetching work queues', error);
    } finally {
      setLoadingState((prev) => ({ ...prev, queues: false }));
    }
  }, [user, selectedQueueId]);

  const fetchQueueDetail = useCallback(async (queueId) => {
    setLoadingState((prev) => ({ ...prev, tasks: true }));
    try {
      const [detailRes, tasksRes, subsRes] = await Promise.all([
        api.get(API_ENDPOINTS.workQueues.detail(queueId)),
        api.get(API_ENDPOINTS.workQueues.tasks(queueId)),
        api.get(API_ENDPOINTS.workQueues.subscriptions(queueId)),
      ]);
      setSelectedQueue(detailRes.data);
      setQueueTasks(tasksRes.data || []);
      setQueueSubscriptions(subsRes.data || []);
    } catch (error) {
      console.error('Error fetching queue details', error);
    } finally {
      setLoadingState((prev) => ({ ...prev, tasks: false }));
    }
  }, []);

  const fetchUserWorkspace = useCallback(async () => {
    if (!user) return;
    setLoadingState((prev) => ({ ...prev, inbox: true }));
    try {
      const [inboxRes, reservedRes, countsRes] = await Promise.all([
        api.get(API_ENDPOINTS.tasks.inbox(user.username)),
        api.get(API_ENDPOINTS.tasks.reserved(user.username)),
        api.get(API_ENDPOINTS.tasks.counts(user.username)),
      ]);
      setInboxTasks(inboxRes.data || []);
      setReservedTasks(reservedRes.data || []);
      setTaskCounts({
        open: countsRes.data?.open ?? 0,
        reserved: countsRes.data?.reserved ?? 0,
        inProgress: countsRes.data?.inProgress ?? 0,
        deferred: countsRes.data?.deferred ?? 0,
        closed: countsRes.data?.closed ?? 0,
      });
    } catch (error) {
      console.error('Error fetching user workspace tasks', error);
    } finally {
      setLoadingState((prev) => ({ ...prev, inbox: false }));
    }
  }, [user]);

  const handleReserveNext = async (count) => {
    if (!selectedQueueId || !user) return;
    setActionLoading(true);
    try {
      await api.post(API_ENDPOINTS.workQueues.reserve(selectedQueueId), {
        username: user.username,
        count,
      });
      await Promise.all([fetchQueueDetail(selectedQueueId), fetchUserWorkspace()]);
    } catch (error) {
      console.error('Error reserving next tasks', error);
      alert('Failed to reserve tasks. Please try again.');
    } finally {
      setActionLoading(false);
    }
  };

  const handleReserveTask = async (taskId) => {
    if (!user) return;
    setActionLoading(true);
    try {
      await api.post(API_ENDPOINTS.tasks.reserve(taskId), { username: user.username });
      await Promise.all([fetchQueueDetail(selectedQueueId), fetchUserWorkspace()]);
    } catch (error) {
      console.error('Error reserving task', error);
      alert(error.response?.data?.message || 'Failed to reserve task');
    } finally {
      setActionLoading(false);
    }
  };

  const handleUnreserveTask = async (taskId) => {
    if (!user) return;
    setActionLoading(true);
    try {
      await api.post(API_ENDPOINTS.tasks.unreserve(taskId), { username: user.username });
      await Promise.all([fetchQueueDetail(selectedQueueId), fetchUserWorkspace()]);
    } catch (error) {
      console.error('Error unreserving task', error);
      alert(error.response?.data?.message || 'Failed to unreserve task');
    } finally {
      setActionLoading(false);
    }
  };

  const handleSubscribe = async () => {
    if (!selectedQueueId || !user) return;
    setActionLoading(true);
    try {
      await api.post(API_ENDPOINTS.workQueues.subscribe(selectedQueueId), { userId: user.username });
      await Promise.all([fetchQueueDetail(selectedQueueId), fetchQueues()]);
    } catch (error) {
      console.error('Error subscribing to queue', error);
      alert(error.response?.data?.message || 'Failed to subscribe');
    } finally {
      setActionLoading(false);
    }
  };

  const handleUnsubscribe = async () => {
    if (!selectedQueueId || !user) return;
    setActionLoading(true);
    try {
      await api.delete(API_ENDPOINTS.workQueues.unsubscribe(selectedQueueId, user.username));
      await Promise.all([fetchQueueDetail(selectedQueueId), fetchQueues()]);
    } catch (error) {
      console.error('Error unsubscribing from queue', error);
      alert(error.response?.data?.message || 'Failed to unsubscribe');
    } finally {
      setActionLoading(false);
    }
  };

  const openTaskDetail = async (task) => {
    setSelectedTask(task);
    setSelectedTaskHistory([]);
    if (!task) return;
    setHistoryLoading(true);
    try {
      const res = await api.get(API_ENDPOINTS.tasks.history(task.id));
      setSelectedTaskHistory(res.data || []);
    } catch (error) {
      console.error('Error fetching task history', error);
    } finally {
      setHistoryLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const renderQueueList = () => (
    <div className="queue-table-card">
      <div className="queue-table-header">
        <h2>Work Queues</h2>
        <button className="btn btn-outline" onClick={fetchQueues}>Refresh</button>
      </div>
      <div className="queue-table-wrapper">
        <table className="table queue-table">
          <thead>
            <tr>
              <th>Action</th>
              <th>Name</th>
              <th>Administrator</th>
              <th>User Subscription</th>
              <th>Tasks</th>
            </tr>
          </thead>
          <tbody>
            {queues.map((queue) => (
              <tr key={queue.id} className={queue.id === selectedQueueId ? 'selected' : ''}>
                <td>
                  <button className="link-btn" onClick={() => setSelectedQueueId(queue.id)}>View</button>
                </td>
                <td>{formatQueueName(queue.name)}</td>
                <td>{queue.administratorUserId || '—'}</td>
                <td>{queue.allowSubscription ? 'Yes' : 'No'}</td>
                <td>{queueCounts[queue.name] ?? 0}</td>
              </tr>
            ))}
            {queues.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center py-4 text-muted">No work queues configured.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderQueueDetail = () => {
    if (!selectedQueue) {
      return (
        <div className="queue-detail-empty">
          <p>Select a work queue to see details.</p>
        </div>
      );
    }

    return (
      <div className="queue-detail">
        <div className="queue-detail-header">
          <div>
            <h2>{formatQueueName(selectedQueue.name)}</h2>
            <p className="queue-description">{selectedQueue.description || 'No description available.'}</p>
          </div>
          <div className="queue-header-actions">
            {isSubscribedToQueue ? (
              <button className="btn btn-outline" onClick={handleUnsubscribe} disabled={actionLoading}>
                Unsubscribe
              </button>
            ) : (
              <button className="btn btn-primary" onClick={handleSubscribe} disabled={actionLoading || !selectedQueue.allowSubscription}>
                Subscribe
              </button>
            )}
          </div>
        </div>

        <div className="queue-metadata">
          <div>
            <label>Administrator</label>
            <span>{selectedQueue.administratorUserId || '—'}</span>
          </div>
          <div>
            <label>Sensitivity</label>
            <span>{selectedQueue.sensitivityLevel ?? '—'}</span>
          </div>
          <div>
            <label>Subscriptions Allowed</label>
            <span>{selectedQueue.allowSubscription ? 'Yes' : 'No'}</span>
          </div>
          <div>
            <label>My Subscription</label>
            <span>{isSubscribedToQueue ? 'Active' : 'Not subscribed'}</span>
          </div>
        </div>

        <div className="queue-toolbar">
          <div className="toolbar-left">
            <button className="btn btn-primary" onClick={() => handleReserveNext(1)} disabled={!isSubscribedToQueue || actionLoading}>
              Reserve Next Task
            </button>
            <button className="btn btn-primary" onClick={() => handleReserveNext(5)} disabled={!isSubscribedToQueue || actionLoading}>
              Reserve Next 5 Tasks
            </button>
            <button className="btn btn-primary" onClick={() => handleReserveNext(20)} disabled={!isSubscribedToQueue || actionLoading}>
              Reserve Next 20 Tasks
            </button>
          </div>
          <div className="toolbar-right">
            <span>Total Tasks: {queueTasks.length}</span>
          </div>
        </div>

        <div className="queue-content-tabs">
          <button
            className={`queue-tab ${queueTab === 'tasks' ? 'active' : ''}`}
            onClick={() => setQueueTab('tasks')}
          >
            Work Queue Tasks
          </button>
          <button
            className={`queue-tab ${queueTab === 'subscriptions' ? 'active' : ''}`}
            onClick={() => setQueueTab('subscriptions')}
          >
            Queue Subscriptions
          </button>
          <button
            className={`queue-tab ${queueTab === 'comments' ? 'active' : ''}`}
            onClick={() => setQueueTab('comments')}
          >
            Comments
          </button>
        </div>

        {queueTab === 'tasks' && renderQueueTaskTable()}
        {queueTab === 'subscriptions' && renderQueueSubscriptionTable()}
        {queueTab === 'comments' && renderQueueComments()}
      </div>
    );
  };

  const renderQueueTaskTable = () => (
    <div className="queue-tasks-table">
      <table className="table">
        <thead>
          <tr>
            <th>Action</th>
            <th>Task ID</th>
            <th>Subject</th>
            <th>Priority</th>
            <th>Status</th>
            <th>Reserved By</th>
            <th>Deadline</th>
          </tr>
        </thead>
        <tbody>
          {queueTasks.map((task) => (
            <tr key={task.id}>
              <td>
                {task.reservedBy ? (
                  <span className="badge badge-secondary">Reserved</span>
                ) : (
                  <button
                    className="link-btn"
                    onClick={() => handleReserveTask(task.id)}
                    disabled={!isSubscribedToQueue || actionLoading}
                  >
                    Reserve…
                  </button>
                )}
              </td>
              <td>
                <button className="link-btn" onClick={() => openTaskDetail(task)}>
                  {task.id}
                </button>
              </td>
              <td>{task.title}</td>
              <td>{task.priority}</td>
              <td>{task.status}</td>
              <td>{task.reservedBy || '—'}</td>
              <td>{task.dueDate ? formatDate(task.dueDate) : '—'}</td>
            </tr>
          ))}
          {queueTasks.length === 0 && (
            <tr>
              <td colSpan="7" className="text-center py-4 text-muted">
                No tasks available in this queue.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );

  const renderQueueSubscriptionTable = () => (
    <div className="queue-subscriptions-table">
      <table className="table">
        <thead>
          <tr>
            <th>User</th>
            <th>Status</th>
            <th>Subscribed At</th>
            <th>Unsubscribed At</th>
          </tr>
        </thead>
        <tbody>
          {queueSubscriptions.map((sub) => (
            <tr key={sub.id}>
              <td>{sub.userId}</td>
              <td>{sub.active ? 'Active' : 'Inactive'}</td>
              <td>{sub.subscribedAt ? formatDateTime(sub.subscribedAt) : '—'}</td>
              <td>{sub.unsubscribedAt ? formatDateTime(sub.unsubscribedAt) : '—'}</td>
            </tr>
          ))}
          {queueSubscriptions.length === 0 && (
            <tr>
              <td colSpan="4" className="text-center py-4 text-muted">
                No subscriptions for this queue.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );

  const renderQueueComments = () => (
    <div className="queue-comments">
      <p>{selectedQueue?.comments || 'No comments documented for this queue.'}</p>
    </div>
  );

  const renderInbox = () => (
    <div className="queue-inbox">
      <div className="queue-inbox-header">
        <h2>My Inbox</h2>
        <button className="btn btn-outline" onClick={fetchUserWorkspace}>Refresh</button>
      </div>
      <div className="inbox-counts">
        <div className="count-card">
          <span className="count-value">{taskCounts.open}</span>
          <span className="count-label">Open</span>
        </div>
        <div className="count-card">
          <span className="count-value">{taskCounts.reserved}</span>
          <span className="count-label">Reserved</span>
        </div>
        <div className="count-card">
          <span className="count-value">{taskCounts.inProgress}</span>
          <span className="count-label">In Progress</span>
        </div>
        <div className="count-card">
          <span className="count-value">{taskCounts.deferred}</span>
          <span className="count-label">Deferred</span>
        </div>
        <div className="count-card">
          <span className="count-value">{taskCounts.closed}</span>
          <span className="count-label">Closed</span>
        </div>
      </div>
      <div className="queue-inbox-table">
        <TaskTable tasks={inboxTasks} onOpenTask={openTaskDetail} />
      </div>
    </div>
  );

  const renderReserved = () => (
    <div className="queue-inbox">
      <div className="queue-inbox-header">
        <h2>Reserved Tasks</h2>
        <button className="btn btn-outline" onClick={fetchUserWorkspace}>Refresh</button>
      </div>
      <div className="queue-inbox-table">
        <table className="table">
          <thead>
            <tr>
              <th>Action</th>
              <th>Task ID</th>
              <th>Subject</th>
              <th>Priority</th>
              <th>Status</th>
              <th>Queue</th>
              <th>Due</th>
            </tr>
          </thead>
          <tbody>
            {reservedTasks.map((task) => (
              <tr key={task.id}>
                <td>
                  <button className="link-btn" onClick={() => handleUnreserveTask(task.id)} disabled={actionLoading}>
                    Unreserve…
                  </button>
                </td>
                <td>
                  <button className="link-btn" onClick={() => openTaskDetail(task)}>
                    {task.id}
                  </button>
                </td>
                <td>{task.title}</td>
                <td>{task.priority}</td>
                <td>{task.status}</td>
                <td>{formatQueueName(task.workQueue)}</td>
                <td>{task.dueDate ? formatDate(task.dueDate) : '—'}</td>
              </tr>
            ))}
            {reservedTasks.length === 0 && (
              <tr>
                <td colSpan="7" className="text-center py-4 text-muted">
                  You do not have any reserved tasks.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="workqueue-page">
      <div className="ca-header">
        <div className="container">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <div className="ca-logo">CA</div>
              <div>
                <h1 className="text-xl font-bold text-ca-primary-900">CMIPS Work Queue Management</h1>
                <p className="text-sm text-ca-primary-600">Manage tasks, subscriptions, and inbox</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <NotificationCenter userId={user?.username} />
              <span className="text-sm text-ca-primary-700">
                Welcome, <strong>{user?.username}</strong>
              </span>
              <button onClick={handleLogout} className="btn btn-outline">
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="workqueue-container">
        <aside className="workqueue-sidebar">
          <div className="sidebar-section">
            <h3>Shortcuts</h3>
            <nav>
              <button className={`sidebar-link ${viewMode === 'queues' ? 'active' : ''}`} onClick={() => setViewMode('queues')}>
                Work Queues
              </button>
              <button className={`sidebar-link ${viewMode === 'inbox' ? 'active' : ''}`} onClick={() => setViewMode('inbox')}>
                Inbox
              </button>
              <button className={`sidebar-link ${viewMode === 'reserved' ? 'active' : ''}`} onClick={() => setViewMode('reserved')}>
                Reserved Tasks
              </button>
            </nav>
          </div>
          <div className="sidebar-section">
            <h3>My Subscriptions</h3>
            <ul>
              {userSubscriptions.map((subscription) => (
                <li key={subscription.id}>
                  <button
                    className={`sidebar-link ${subscription.queueId === selectedQueueId ? 'active' : ''}`}
                    onClick={() => {
                      setSelectedQueueId(subscription.queueId);
                      setViewMode('queues');
                    }}
                  >
                    {formatQueueName(queues.find((q) => q.id === subscription.queueId)?.name || subscription.queueId)}
                  </button>
                </li>
              ))}
              {userSubscriptions.length === 0 && (
                <li className="text-muted small">No subscriptions yet.</li>
              )}
            </ul>
          </div>
        </aside>

        <main className="workqueue-main">
          {loadingState.queues && queues.length === 0 ? (
            <div className="loading-state">Loading queues...</div>
          ) : viewMode === 'inbox' ? (
            renderInbox()
          ) : viewMode === 'reserved' ? (
            renderReserved()
          ) : (
            <div className="queue-content">
              {renderQueueList()}
              {loadingState.tasks && !queueTasks.length ? (
                <div className="loading-state">Loading queue details...</div>
              ) : (
                renderQueueDetail()
              )}
            </div>
          )}
        </main>
      </div>

      {selectedTask && (
        <TaskDrawer
          task={selectedTask}
          history={selectedTaskHistory}
          onClose={() => setSelectedTask(null)}
          loading={historyLoading}
        />
      )}
    </div>
  );
};

const TaskTable = ({ tasks, onOpenTask }) => (
  <table className="table">
    <thead>
      <tr>
        <th>Task ID</th>
        <th>Subject</th>
        <th>Priority</th>
        <th>Status</th>
        <th>Queue</th>
        <th>Due</th>
      </tr>
    </thead>
    <tbody>
      {tasks.map((task) => (
        <tr key={task.id}>
          <td>
            <button className="link-btn" onClick={() => onOpenTask(task)}>
              {task.id}
            </button>
          </td>
          <td>{task.title}</td>
          <td>{task.priority}</td>
          <td>{task.status}</td>
          <td>{formatQueueName(task.workQueue)}</td>
          <td>{task.dueDate ? formatDate(task.dueDate) : '—'}</td>
        </tr>
      ))}
      {tasks.length === 0 && (
        <tr>
          <td colSpan="6" className="text-center py-4 text-muted">
            No tasks to display.
          </td>
        </tr>
      )}
    </tbody>
  </table>
);

const TaskDrawer = ({ task, history, onClose, loading }) => (
  <div className="task-drawer-overlay" onClick={onClose}>
    <div className="task-drawer" onClick={(e) => e.stopPropagation()}>
      <div className="task-drawer-header">
        <div>
          <h2>{task.title}</h2>
          <p className="task-drawer-subtitle">Task ID: {task.id}</p>
        </div>
        <button className="close-btn" onClick={onClose}>✕</button>
      </div>
      <div className="task-drawer-body">
        <section>
          <h3>Details</h3>
          <div className="detail-grid">
            <DetailItem label="Status" value={task.status} />
            <DetailItem label="Priority" value={task.priority} />
            <DetailItem label="Assigned To" value={task.assignedTo} />
            <DetailItem label="Reserved By" value={task.reservedBy || '—'} />
            <DetailItem label="Work Queue" value={formatQueueName(task.workQueue)} />
            <DetailItem label="Due Date" value={task.dueDate ? formatDateTime(task.dueDate) : '—'} />
            <DetailItem label="Last Action" value={task.lastActionAt ? formatDateTime(task.lastActionAt) : '—'} />
          </div>
        </section>
        <section>
          <h3>Description</h3>
          <p>{task.description || 'No description provided.'}</p>
        </section>
        {task.actionLink && (
          <section>
            <h3>Primary Action</h3>
            <a className="btn btn-primary" href={task.actionLink} target="_blank" rel="noreferrer">
              Open Related Entity
            </a>
          </section>
        )}
        <section>
          <h3>Timeline</h3>
          {loading ? (
            <p>Loading history…</p>
          ) : history.length === 0 ? (
            <p className="text-muted">No history recorded.</p>
          ) : (
            <ul className="history-list">
              {history.map((event) => (
                <li key={event.id}>
                  <div className="history-item">
                    <span className="history-event">{event.eventType}</span>
                    <span className="history-time">{formatDateTime(event.eventTime)}</span>
                  </div>
                  <div className="history-details">
                    <DetailItem label="From" value={event.fromUser || '—'} />
                    <DetailItem label="To" value={event.toUser || '—'} />
                  </div>
                  {event.comments && <p className="history-comment">{event.comments}</p>}
                </li>
              ))}
            </ul>
          )}
        </section>
      </div>
    </div>
  </div>
);

const DetailItem = ({ label, value }) => (
  <div className="detail-item">
    <label>{label}</label>
    <span>{value}</span>
  </div>
);

const formatDate = (value) => {
  try {
    return new Date(value).toLocaleDateString();
  } catch {
    return value;
  }
};

const formatDateTime = (value) => {
  try {
    return new Date(value).toLocaleString();
  } catch {
    return value;
  }
};

const formatQueueName = (value) => {
  if (!value) return '—';
  return value
    .replace(/_/g, ' ')
    .replace(/\b\w/g, (c) => c.toUpperCase());
};

export default WorkQueuePage;
