import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { apiClient as api, API_ENDPOINTS } from '../config/api';
import NotificationCenter from './NotificationCenter';

const CaseWorkerDashboard = () => {
  const { user, logout, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const username = user?.username;

  const [isInitializing, setIsInitializing] = useState(true);
  const [loadingStates, setLoadingStates] = useState({
    tasks: false,
    queues: false,
    notifications: false,
  });
  const [errors, setErrors] = useState({
    global: '',
    queues: '',
  });

  const [taskCounts, setTaskCounts] = useState({
    open: 0,
    reserved: 0,
    inProgress: 0,
    deferred: 0,
    escalated: 0,
    closed: 0,
  });

  const [assignedTasks, setAssignedTasks] = useState([]);
  const [reservedTasks, setReservedTasks] = useState([]);
  const [deferredTasks, setDeferredTasks] = useState([]);
  const [inboxTasks, setInboxTasks] = useState([]);
  const [notifications, setNotifications] = useState([]);

  const [queueSubscriptions, setQueueSubscriptions] = useState([]);
  const [queueTasks, setQueueTasks] = useState({});

  const queueNameLookup = useMemo(() => {
    const map = new Map();
    queueSubscriptions.forEach((subscription) => {
      if (subscription.queueCode) {
        map.set(subscription.queueCode, subscription.queueName || prettifyQueueKey(subscription.queueCode));
      }
      if (subscription.queueName) {
        map.set(subscription.queueName, subscription.queueName);
      }
    });
    return map;
  }, [queueSubscriptions]);

  const getQueueDisplayName = useCallback(
    (key) => {
      if (!key) return '—';
      const normalized = key.toString();
      return (
        queueNameLookup.get(normalized) ||
        queueNameLookup.get(normalized.toUpperCase()) ||
        prettifyQueueKey(normalized)
      );
    },
    [queueNameLookup]
  );

  const fetchNotifications = useCallback(async () => {
    if (!username) return;
    setLoadingStates((prev) => ({ ...prev, notifications: true }));
    try {
      const response = await api.get(`/notifications/user/${username}`);
      setNotifications((response.data || []).slice(0, 5));
    } catch (error) {
      console.error('Failed to load notifications', error);
      setNotifications([]);
    } finally {
      setLoadingStates((prev) => ({ ...prev, notifications: false }));
    }
  }, [username]);

  const fetchTaskData = useCallback(async () => {
    if (!username) return;
    setLoadingStates((prev) => ({ ...prev, tasks: true }));
    try {
      const [allTasksRes, reservedRes, countsRes, inboxRes] = await Promise.all([
        api.get(API_ENDPOINTS.tasks.list(username)),
        api.get(API_ENDPOINTS.tasks.reserved(username)),
        api.get(API_ENDPOINTS.tasks.counts(username)),
        api.get(API_ENDPOINTS.tasks.inbox(username)),
      ]);

      const allTasks = allTasksRes.data || [];
      const reserved = reservedRes.data || [];
      const counts = countsRes.data || {};
      const inbox = inboxRes.data || [];

      setAssignedTasks(
        allTasks.filter((task) =>
          ['OPEN', 'IN_PROGRESS', 'ESCALATED'].includes(task.status)
        )
      );
      setReservedTasks(reserved);
      setDeferredTasks(allTasks.filter((task) => task.status === 'DEFERRED'));
      setInboxTasks(inbox);
      setTaskCounts({
        open: counts.open ?? 0,
        reserved: counts.reserved ?? 0,
        inProgress: counts.inProgress ?? 0,
        deferred: counts.deferred ?? 0,
        escalated: allTasks.filter((task) => task.status === 'ESCALATED').length,
        closed: counts.closed ?? 0,
      });
    } catch (error) {
      console.error('Failed to load tasks', error);
      setErrors((prev) => ({
        ...prev,
        global: 'Unable to load task data. Please refresh the page.',
      }));
      setAssignedTasks([]);
      setReservedTasks([]);
      setDeferredTasks([]);
      setInboxTasks([]);
      setTaskCounts({
        open: 0,
        reserved: 0,
        inProgress: 0,
        deferred: 0,
        escalated: 0,
        closed: 0,
      });
    } finally {
      setLoadingStates((prev) => ({ ...prev, tasks: false }));
    }
  }, [username]);

  const fetchQueueData = useCallback(async () => {
    if (!username) return;
    setLoadingStates((prev) => ({ ...prev, queues: true }));
    try {
      const subscriptionsRes = await api.get(
        API_ENDPOINTS.workQueues.userSubscriptions(username)
      );
      const subscriptions = subscriptionsRes.data || [];
      setQueueSubscriptions(subscriptions);

      const queueTaskEntries = await Promise.all(
        subscriptions.map(async (sub) => {
          try {
            const tasksRes = await api.get(
              API_ENDPOINTS.workQueues.tasks(sub.queueId)
            );
            return [sub.queueId, (tasksRes.data || []).slice(0, 10)];
          } catch (error) {
            console.error('Failed to load tasks for queue', sub.queueId, error);
            return [sub.queueId, []];
          }
        })
      );

      const queueTaskMap = Object.fromEntries(queueTaskEntries);
      setQueueTasks(queueTaskMap);
      setErrors((prev) => ({ ...prev, queues: '' }));
    } catch (error) {
      console.error('Failed to load queue subscriptions', error);
      setQueueSubscriptions([]);
      setQueueTasks({});
      setErrors((prev) => ({
        ...prev,
        queues: 'Unable to load your work queues.',
      }));
    } finally {
      setLoadingStates((prev) => ({ ...prev, queues: false }));
    }
  }, [username]);

  const bootstrap = useCallback(async () => {
    try {
      await Promise.all([fetchTaskData(), fetchQueueData(), fetchNotifications()]);
      setErrors((prev) => ({ ...prev, global: '' }));
    } finally {
      setIsInitializing(false);
    }
  }, [fetchTaskData, fetchQueueData, fetchNotifications]);

  useEffect(() => {
    if (authLoading) {
      return;
    }
    if (!user || user.role !== 'CASE_WORKER') {
      navigate('/login');
      return;
    }
    bootstrap();
  }, [authLoading, user, navigate, bootstrap]);

  const refreshAfterAction = useCallback(async () => {
    await Promise.all([fetchTaskData(), fetchQueueData()]);
  }, [fetchTaskData, fetchQueueData]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleOpenTask = useCallback((task) => {
    if (task.actionLink) {
      window.location.href = task.actionLink;
      return;
    }
    if (task.relatedEntityId) {
      navigate(`/cases/${task.relatedEntityId}`);
    } else {
      alert('This task does not have a direct action link.');
    }
  }, [navigate]);

  const handleReserveTask = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    try {
      await api.post(API_ENDPOINTS.tasks.reserve(taskId), { username });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to reserve task', error);
      alert('Unable to reserve this task.');
    }
  }, [username, refreshAfterAction]);

  const handleUnreserveTask = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    try {
      await api.post(API_ENDPOINTS.tasks.unreserve(taskId), { username });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to release task', error);
      alert('Unable to release this task.');
    }
  }, [username, refreshAfterAction]);

  const handleForwardTask = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    const toUser = window.prompt('Forward task to user (username):');
    if (!toUser) return;
    const comments =
      window.prompt('Comment (optional):', 'Forwarded from caseworker workspace') || '';
    try {
      await api.post(API_ENDPOINTS.tasks.forward(taskId), {
        fromUser: username,
        toUser,
        comments,
      });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to forward task', error);
      alert('Unable to forward this task.');
    }
  }, [username, refreshAfterAction]);

  const handleCloseTask = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    const comments =
      window.prompt('Add comment before closing:', 'Closed by caseworker.') || '';
    try {
      await api.post(API_ENDPOINTS.tasks.close(taskId), {
        username,
        comments,
      });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to close task', error);
      alert('Unable to close this task.');
    }
  }, [username, refreshAfterAction]);

  const handleDeferTask = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    const dateInput = window.prompt(
      'Defer until (YYYY-MM-DD or leave blank to cancel):'
    );
    if (!dateInput) return;
    const reason = window.prompt('Reason for deferral:') || 'Deferred by caseworker.';
    try {
      const restartAt = new Date(dateInput);
      if (Number.isNaN(restartAt.getTime())) {
        alert('Invalid date provided.');
        return;
      }
      await api.post(API_ENDPOINTS.tasks.defer(taskId), {
        username,
        restartAt: restartAt.toISOString(),
        reason,
      });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to defer task', error);
      alert('Unable to defer this task.');
    }
  }, [username, refreshAfterAction]);

  const handleRestartTask = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    const comments =
      window.prompt('Add comment before restarting:', 'Restarted by caseworker.') || '';
    try {
      await api.post(API_ENDPOINTS.tasks.restart(taskId), {
        username,
        comments,
      });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to restart task', error);
      alert('Unable to restart this task.');
    }
  }, [username, refreshAfterAction]);

  const handleAddComment = useCallback(async (taskId) => {
    if (!taskId || !username) return;
    const comments = window.prompt('Enter comment for this task:');
    if (!comments) return;
    try {
      await api.post(API_ENDPOINTS.tasks.comment(taskId), {
        username,
        comments,
      });
      await refreshAfterAction();
    } catch (error) {
      console.error('Failed to add comment', error);
      alert('Unable to add comment.');
    }
  }, [username, refreshAfterAction]);

  const handleReserveNextFromQueue = useCallback(
    (queueId) => {
      const tasks = queueTasks[queueId] || [];
      if (tasks.length === 0) {
        alert('No tasks available to reserve from this queue.');
        return;
      }
      handleReserveTask(tasks[0].id);
    },
    [queueTasks, handleReserveTask]
  );

  const summaryCards = useMemo(
    () => [
      { label: 'Open Tasks', value: taskCounts.open },
      { label: 'Reserved', value: taskCounts.reserved },
      { label: 'Deferred', value: taskCounts.deferred },
      { label: 'Escalated', value: taskCounts.escalated },
    ],
    [taskCounts]
  );

  if (isInitializing) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-ca-secondary-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-ca-highlight-600 mx-auto"></div>
          <p className="mt-4 text-ca-primary-600">Loading Caseworker Workspace…</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-ca-secondary-50">
      <div className="ca-header">
        <div className="container">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <div className="ca-logo">CA</div>
              <div>
                <h1 className="text-xl font-bold text-ca-primary-900">Caseworker Workspace</h1>
                <p className="text-sm text-ca-primary-600">
                  Manage your tasks, queues, and case workload
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <NotificationCenter userId={username} />
              <span className="text-sm text-ca-primary-700">
                Welcome, <strong>{username}</strong> (Caseworker)
              </span>
              <button onClick={handleLogout} className="btn btn-outline">
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="container py-6 space-y-6">
        {errors.global && (
          <div className="p-4 bg-red-50 border border-red-200 text-red-700 rounded">
            {errors.global}
          </div>
        )}

        <section className="grid lg:grid-cols-4 md:grid-cols-2 gap-4">
          {summaryCards.map((card) => (
            <div key={card.label} className="card">
              <div className="card-body text-center py-6">
                <div className="text-3xl font-bold text-ca-primary-900">{card.value}</div>
                <p className="text-sm text-ca-primary-600 uppercase tracking-wide mt-1">
                  {card.label}
                </p>
              </div>
            </div>
          ))}
        </section>

        <section className="grid xl:grid-cols-3 gap-4">
          <div className="card xl:col-span-2">
            <div className="card-header flex items-center justify-between">
              <h2 className="text-lg font-semibold text-ca-primary-900">My Tasks</h2>
              <button className="btn btn-outline btn-sm" onClick={fetchTaskData}>
                Refresh
              </button>
            </div>
            <div className="card-body space-y-4">
              <TaskSection
                title="Assigned Tasks"
                description="Tasks currently assigned to you."
                loading={loadingStates.tasks}
                tasks={assignedTasks}
                onOpen={handleOpenTask}
                onComment={handleAddComment}
                onClose={handleCloseTask}
                onForward={handleForwardTask}
                onDefer={handleDeferTask}
                showReserve={false}
                showRelease={false}
                queueNameResolver={getQueueDisplayName}
              />
              <TaskSection
                title="Reserved Tasks"
                description="Tasks you have reserved from queues."
                loading={loadingStates.tasks}
                tasks={reservedTasks}
                onOpen={handleOpenTask}
                onComment={handleAddComment}
                onClose={handleCloseTask}
                onForward={handleForwardTask}
                onDefer={handleDeferTask}
                onRelease={handleUnreserveTask}
                showReserve={false}
                showRelease
                queueNameResolver={getQueueDisplayName}
              />
              <TaskSection
                title="Deferred Tasks"
                description="Tasks you have postponed for a later date."
                loading={loadingStates.tasks}
                tasks={deferredTasks}
                onOpen={handleOpenTask}
                onComment={handleAddComment}
                onForward={handleForwardTask}
                onRestart={handleRestartTask}
                showReserve={false}
                showRelease={false}
                queueNameResolver={getQueueDisplayName}
              />
            </div>
          </div>

          <div className="space-y-4">
            <div className="card">
              <div className="card-header flex items-center justify-between">
                <div>
                  <h2 className="text-lg font-semibold text-ca-primary-900">User Inbox</h2>
                  <p className="text-sm text-ca-primary-600">
                    Notifications and task alerts
                  </p>
                </div>
                <button className="btn btn-outline btn-sm" onClick={fetchNotifications}>
                  Refresh
                </button>
              </div>
              <div className="card-body space-y-3">
                {loadingStates.notifications ? (
                  <LoadingIndicator label="Loading notifications" />
                ) : notifications.length === 0 ? (
                  <p className="text-sm text-ca-primary-600">
                    No notifications at this time.
                  </p>
                ) : (
                  notifications.map((note) => (
                    <div
                      key={note.id}
                      className={`p-3 rounded border ${
                        note.readStatus ? 'bg-ca-secondary-50' : 'bg-blue-50 border-blue-300'
                      }`}
                    >
                      <p className="text-sm font-semibold text-ca-primary-900">
                        {note.message}
                      </p>
                      <p className="text-xs text-ca-primary-600 mt-1">
                        {new Date(note.createdAt).toLocaleString()}
                      </p>
                    </div>
                  ))
                )}
              </div>
            </div>

            <div className="card">
              <div className="card-header">
                <h2 className="text-lg font-semibold text-ca-primary-900">Quick Actions</h2>
              </div>
              <div className="card-body space-y-2">
                <button className="btn btn-outline w-full" onClick={() => navigate('/cases')}>
                  Case Search
                </button>
                <button
                  className="btn btn-outline w-full"
                  onClick={() => navigate('/timesheets')}
                >
                  Timesheet Inbox
                </button>
                <button
                  className="btn btn-outline w-full"
                  onClick={() =>
                    alert('Creating ad-hoc tasks is not yet implemented in this build.')
                  }
                >
                  Create User Task
                </button>
              </div>
            </div>

            <div className="card">
              <div className="card-header flex items-center justify-between">
                <div>
                  <h2 className="text-lg font-semibold text-ca-primary-900">Task Inbox</h2>
                  <p className="text-sm text-ca-primary-600">
                    Tasks waiting in your personal inbox.
                  </p>
                </div>
              </div>
              <div className="card-body">
                {loadingStates.tasks ? (
                  <LoadingIndicator label="Loading inbox" />
                ) : inboxTasks.length === 0 ? (
                  <p className="text-sm text-ca-primary-600">
                    No items currently in your inbox.
                  </p>
                ) : (
                  <SimpleTaskList
                    tasks={inboxTasks}
                    onOpen={handleOpenTask}
                    onReserve={handleReserveTask}
                    queueNameResolver={getQueueDisplayName}
                  />
                )}
              </div>
            </div>
          </div>
        </section>

        <section className="card">
          <div className="card-header flex items-center justify-between">
            <div>
              <h2 className="text-lg font-semibold text-ca-primary-900">My Work Queues</h2>
              <p className="text-sm text-ca-primary-600">
                Queues you are subscribed to and available tasks to reserve.
              </p>
            </div>
            <button className="btn btn-outline btn-sm" onClick={fetchQueueData}>
              Refresh
            </button>
          </div>
          <div className="card-body space-y-4">
            {errors.queues && (
              <div className="p-3 bg-yellow-50 border border-yellow-300 text-sm text-yellow-800 rounded">
                {errors.queues}
              </div>
            )}
            {loadingStates.queues ? (
              <LoadingIndicator label="Loading work queues" />
            ) : queueSubscriptions.length === 0 ? (
              <p className="text-sm text-ca-primary-600">
                You are not subscribed to any work queues.
              </p>
            ) : (
              queueSubscriptions.map((subscription) => (
                <QueuePanel
                  key={subscription.queueId}
                  subscription={subscription}
                  tasks={queueTasks[subscription.queueId] || []}
                  onReserveTask={handleReserveTask}
                  onOpenTask={handleOpenTask}
                  onReserveNext={() => handleReserveNextFromQueue(subscription.queueId)}
                />
              ))
            )}
          </div>
        </section>
      </div>
    </div>
  );
};

const TaskSection = ({
  title,
  description,
  loading,
  tasks,
  onOpen,
  onReserve,
  onComment,
  onClose,
  onForward,
  onDefer,
  onRestart,
  onRelease,
  showReserve = true,
  showRelease = false,
  queueNameResolver,
}) => (
  <div className="space-y-2">
    <div className="flex items-center justify-between">
      <div>
        <h3 className="text-md font-semibold text-ca-primary-900">{title}</h3>
        <p className="text-xs text-ca-primary-600">{description}</p>
      </div>
      <span className="text-xs text-ca-primary-600">{tasks.length} item(s)</span>
    </div>
    {loading ? (
      <LoadingIndicator label={`Loading ${title.toLowerCase()}`} />
    ) : tasks.length === 0 ? (
      <p className="text-sm text-ca-primary-600">No tasks in this category.</p>
    ) : (
      <div className="overflow-x-auto">
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Task</th>
              <th>Status</th>
              <th>Priority</th>
              <th>Queue</th>
              <th>Due</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.id}</td>
                <td>{task.title}</td>
                <td>
                  <span className="badge badge-primary">{task.status}</span>
                </td>
                <td>
                  <span className={`badge ${badgeForPriority(task.priority)}`}>
                    {task.priority}
                  </span>
                </td>
                <td>{(queueNameResolver && queueNameResolver(task.workQueue)) || prettifyQueueKey(task.workQueue) || '—'}</td>
                <td>
                  {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : '—'}
                </td>
                <td>
                  <div className="flex flex-wrap gap-2">
                    <button className="btn btn-outline btn-xs" onClick={() => onOpen(task)}>
                      Open
                    </button>
                    {showReserve && onReserve && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onReserve(task.id)}
                      >
                        Reserve
                      </button>
                    )}
                    {showRelease && onRelease && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onRelease(task.id)}
                      >
                        Release
                      </button>
                    )}
                    {onComment && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onComment(task.id)}
                      >
                        Comment
                      </button>
                    )}
                    {onDefer && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onDefer(task.id)}
                      >
                        Defer
                      </button>
                    )}
                    {onRestart && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onRestart(task.id)}
                      >
                        Restart
                      </button>
                    )}
                    {onForward && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onForward(task.id)}
                      >
                        Forward
                      </button>
                    )}
                    {onClose && (
                      <button
                        className="btn btn-outline btn-xs"
                        onClick={() => onClose(task.id)}
                      >
                        Close
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )}
  </div>
);

const SimpleTaskList = ({ tasks, onOpen, onReserve, queueNameResolver }) => (
  <div className="space-y-3">
    {tasks.map((task) => (
      <div
        key={task.id}
        className="p-3 bg-ca-secondary-50 border border-ca-secondary-200 rounded"
      >
        <div className="flex justify-between items-center">
          <div>
            <p className="text-sm font-semibold text-ca-primary-900">{task.title}</p>
            <p className="text-xs text-ca-primary-600">
              {(queueNameResolver && queueNameResolver(task.workQueue)) || prettifyQueueKey(task.workQueue) || 'No queue'} •{' '}
              {task.dueDate ? new Date(task.dueDate).toLocaleDateString() : 'No due date'}
            </p>
          </div>
          <div className="flex gap-2">
            <button className="btn btn-outline btn-xs" onClick={() => onOpen(task)}>
              Open
            </button>
            <button className="btn btn-outline btn-xs" onClick={() => onReserve(task.id)}>
              Reserve
            </button>
          </div>
        </div>
      </div>
    ))}
  </div>
);

const QueuePanel = ({ subscription, tasks, onReserveNext, onReserveTask, onOpenTask }) => (
  <div className="border border-ca-secondary-200 rounded p-4 space-y-3 bg-white">
    <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-2">
      <div>
        <h3 className="text-md font-semibold text-ca-primary-900">
          {subscription.queueName || `Queue ${subscription.queueId}`}
        </h3>
        {subscription.queueDescription && (
          <p className="text-xs text-ca-primary-600">
            {subscription.queueDescription}
          </p>
        )}
        {subscription.queueCode && (
          <p className="text-xs text-ca-primary-500 uppercase tracking-wide mt-1">
            Code: {subscription.queueCode} • Sensitivity L{subscription.sensitivityLevel ?? '—'}
          </p>
        )}
      </div>
      <div className="flex gap-2">
        <button className="btn btn-outline btn-xs" onClick={onReserveNext}>
          Reserve Next Task
        </button>
      </div>
    </div>
    {tasks.length === 0 ? (
      <p className="text-sm text-ca-primary-600">No available tasks at the moment.</p>
    ) : (
      <div className="overflow-x-auto">
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Task</th>
              <th>Priority</th>
              <th>Created</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.id}</td>
                <td>{task.title}</td>
                <td>
                  <span className={`badge ${badgeForPriority(task.priority)}`}>
                    {task.priority}
                  </span>
                </td>
                <td>
                  {task.createdAt ? new Date(task.createdAt).toLocaleString() : '—'}
                </td>
                <td>
                  <div className="flex gap-2">
                    <button
                      className="btn btn-outline btn-xs"
                      onClick={() => onOpenTask(task)}
                    >
                      Details
                    </button>
                    <button
                      className="btn btn-outline btn-xs"
                      onClick={() => onReserveTask(task.id)}
                    >
                      Reserve
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )}
  </div>
);

const LoadingIndicator = ({ label }) => (
  <div className="flex items-center space-x-3 text-sm text-ca-primary-600">
    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-ca-highlight-600"></div>
    <span>{label}…</span>
  </div>
);

const badgeForPriority = (priority) => {
  switch (priority) {
    case 'HIGH':
      return 'badge-danger';
    case 'LOW':
      return 'badge-success';
    default:
      return 'badge-warning';
  }
};

const prettifyQueueKey = (value) => {
  if (!value) return undefined;
  return value
    .toString()
    .replace(/_/g, ' ')
    .replace(/\b\w/g, (c) => c.toUpperCase());
};

export default CaseWorkerDashboard;
