import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import NotificationCenter from './NotificationCenter';
import { apiClient, API_ENDPOINTS } from '../config/api';

const TAB_HOME = 'home';
const TAB_USERS = 'users';

const SupervisorDashboard = () => {
  const { user, logout, loading: authLoading } = useAuth();
  const navigate = useNavigate();

  const [isInitializing, setIsInitializing] = useState(true);
  const [activeTab, setActiveTab] = useState(TAB_HOME);
  const [summary, setSummary] = useState(null);
  const [teamUsers, setTeamUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [userDetail, setUserDetail] = useState(null);
  const [errors, setErrors] = useState({ global: '', user: '' });
  const [loadingStates, setLoadingStates] = useState({
    summary: false,
    users: false,
    userDetail: false,
  });

  useEffect(() => {
    if (authLoading) {
      return;
    }
    if (!user || !user.roles?.includes('SUPERVISOR')) {
      navigate('/login');
      return;
    }
    bootstrap();
  }, [authLoading, user, navigate]);

  const bootstrap = async () => {
    try {
      await Promise.all([fetchSummary(), fetchTeamUsers()]);
    } finally {
      setIsInitializing(false);
    }
  };

  const fetchSummary = async () => {
    setLoadingStates((prev) => ({ ...prev, summary: true }));
    try {
      const response = await apiClient.get(API_ENDPOINTS.supervisor.homeSummary);
      setSummary(response.data);
      setErrors((prev) => ({ ...prev, global: '' }));
    } catch (error) {
      console.error('Failed to load supervisor summary', error);
      setErrors((prev) => ({
        ...prev,
        global: 'Unable to load Supervisor Workspace data. Please refresh.',
      }));
    } finally {
      setLoadingStates((prev) => ({ ...prev, summary: false }));
    }
  };

  const fetchTeamUsers = async () => {
    setLoadingStates((prev) => ({ ...prev, users: true }));
    try {
      const response = await apiClient.get(API_ENDPOINTS.supervisor.users);
      setTeamUsers(response.data || []);
    } catch (error) {
      console.error('Failed to load team users', error);
      setErrors((prev) => ({
        ...prev,
        global: 'Unable to load team roster.',
      }));
    } finally {
      setLoadingStates((prev) => ({ ...prev, users: false }));
    }
  };

  const fetchUserDetail = async (username) => {
    if (!username) return;
    setSelectedUser(username);
    setLoadingStates((prev) => ({ ...prev, userDetail: true }));
    try {
      const response = await apiClient.get(API_ENDPOINTS.supervisor.userDetail(username));
      setUserDetail(response.data);
      setErrors((prev) => ({ ...prev, user: '' }));
    } catch (error) {
      console.error('Failed to load user detail', error);
      setErrors((prev) => ({
        ...prev,
        user: `Unable to load workspace for ${username}.`,
      }));
      setUserDetail(null);
    } finally {
      setLoadingStates((prev) => ({ ...prev, userDetail: false }));
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleQuickAction = (action) => {
    if (!action) return;
    switch (action.action) {
      case 'NAVIGATE':
        navigate(action.target);
        break;
      case 'SCROLL': {
        const el = document.getElementById(action.target);
        if (el) {
          el.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
        break;
      }
      default:
        alert(action.description);
    }
  };

  const handleReserveTask = async (taskId) => {
    if (!taskId || !user) return;
    try {
      await apiClient.post(API_ENDPOINTS.tasks.reserve(taskId), { username: user.username });
      await Promise.all([fetchSummary(), fetchTeamUsers(), fetchUserDetail(selectedUser)]);
    } catch (error) {
      console.error('Failed to reserve task', error);
      alert('Unable to reserve the selected task.');
    }
  };

  const handleUnreserveTask = async (taskId) => {
    if (!taskId || !user) return;
    try {
      await apiClient.post(API_ENDPOINTS.tasks.unreserve(taskId), { username: user.username });
      await Promise.all([fetchSummary(), fetchTeamUsers(), fetchUserDetail(selectedUser)]);
    } catch (error) {
      console.error('Failed to unreserve task', error);
      alert('Unable to release the selected task.');
    }
  };

  const handleRedirectTask = async (taskId) => {
    if (!taskId || !user) return;
    const toUser = window.prompt('Redirect task to user (username):');
    if (!toUser) return;
    const comments = window.prompt('Reason / comment for redirection:', 'Reassigned by supervisor.');
    try {
      await apiClient.post(API_ENDPOINTS.tasks.forward(taskId), {
        fromUser: selectedUser || user.username,
        toUser,
        comments: comments || 'Redirected from supervisor workspace',
      });
      await Promise.all([fetchSummary(), fetchTeamUsers(), fetchUserDetail(selectedUser)]);
    } catch (error) {
      console.error('Failed to redirect task', error);
      alert('Unable to redirect the task.');
    }
  };

  const handleCloseTask = async (taskId) => {
    if (!taskId || !user) return;
    const comments = window.prompt('Add closing comment:', 'Closed by supervisor.');
    try {
      await apiClient.post(API_ENDPOINTS.tasks.close(taskId), {
        username: user.username,
        comments: comments || '',
      });
      await Promise.all([fetchSummary(), fetchTeamUsers(), fetchUserDetail(selectedUser)]);
    } catch (error) {
      console.error('Failed to close task', error);
      alert('Unable to close the task.');
    }
  };

  const alerts = summary?.alerts || [];
  const escalationQueue = summary?.escalationQueue || [];
  const quickActions = summary?.quickActions || [];
  const teamSummary = summary?.teamSummary;
  const performance = summary?.performanceMetrics;

  const hasTeamUsers = (teamUsers || []).length > 0;

  if (isInitializing) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-ca-secondary-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-ca-highlight-600 mx-auto"></div>
          <p className="mt-4 text-ca-primary-600">Loading Supervisor Workspace…</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-ca-secondary-50">
      <div className="ca-header">
        <div className="container">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <div className="ca-logo">CA</div>
              <div>
                <h1 className="text-xl font-bold text-ca-primary-900">Supervisor Workspace</h1>
                <p className="text-sm text-ca-primary-600">Manage escalations, team workload, and queues</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <NotificationCenter userId={user?.username} />
              <span className="text-sm text-ca-primary-700">
                Welcome, <strong>{user?.username}</strong> (Supervisor)
              </span>
              <button onClick={handleLogout} className="btn btn-outline">
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="container py-6">
        {errors.global && (
          <div className="mb-4 p-4 border border-red-300 bg-red-50 text-sm text-red-700 rounded">
            {errors.global}
          </div>
        )}

        <div className="mb-6 flex flex-wrap gap-2">
          <button
            className={`btn ${activeTab === TAB_HOME ? 'btn-primary' : 'btn-outline'}`}
            onClick={() => setActiveTab(TAB_HOME)}
          >
            SW – Home
          </button>
          <button
            className={`btn ${activeTab === TAB_USERS ? 'btn-primary' : 'btn-outline'}`}
            onClick={() => setActiveTab(TAB_USERS)}
          >
            SW – My Users
          </button>
          <button className="btn btn-outline" onClick={() => navigate('/supervisor/work-queues')}>
            SW – Work Queue Workspace
          </button>
        </div>

        {activeTab === TAB_HOME && (
          <div id="sw-home" className="space-y-6">
            <section className="grid md:grid-cols-4 gap-4">
              <SummaryCard title="Team Members" value={teamSummary?.totalUsers ?? 0} />
              <SummaryCard title="Active Cases" value={teamSummary?.activeCases ?? 0} />
              <SummaryCard title="Open Tasks" value={teamSummary?.openTasks ?? 0} />
              <SummaryCard title="Work Queues" value={teamSummary?.workQueues ?? 0} />
            </section>

            <section className="grid lg:grid-cols-3 gap-4">
              <div className="card lg:col-span-2" id="alerts-panel">
                <div className="card-header">
                  <h2 className="text-lg font-semibold text-ca-primary-900">Alerts & Watch List</h2>
                </div>
                <div className="card-body space-y-3">
                  {loadingStates.summary ? (
                    <LoadingIndicator label="Loading alerts" />
                  ) : alerts.length === 0 ? (
                    <p className="text-sm text-ca-primary-600">No alerts at this time.</p>
                  ) : (
                    alerts.map((alert, index) => (
                      <div
                        key={`${alert.title}-${index}`}
                        className={`p-3 rounded border-l-4 ${
                          alert.severity === 'CRITICAL'
                            ? 'bg-red-50 border-red-500'
                            : alert.severity === 'WARNING'
                            ? 'bg-yellow-50 border-yellow-500'
                            : 'bg-blue-50 border-blue-500'
                        }`}
                      >
                        <p className="font-semibold text-ca-primary-900">{alert.title}</p>
                        <p className="text-sm text-ca-primary-700">{alert.message}</p>
                      </div>
                    ))
                  )}
                </div>
              </div>

              <div className="card">
                <div className="card-header">
                  <h2 className="text-lg font-semibold text-ca-primary-900">Quick Actions</h2>
                </div>
                <div className="card-body space-y-3">
                  {quickActions.map((action) => (
                    <div key={action.label} className="border rounded p-3 bg-ca-secondary-50">
                      <p className="font-semibold text-ca-primary-900 mb-1">{action.label}</p>
                      <p className="text-sm text-ca-primary-600 mb-2">{action.description}</p>
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleQuickAction(action)}
                      >
                        Execute
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </section>

            <section className="grid lg:grid-cols-3 gap-4">
              <div className="card">
                <div className="card-header">
                  <h2 className="text-lg font-semibold text-ca-primary-900">Performance Metrics</h2>
                </div>
                <div className="card-body space-y-3">
                  <MetricRow label="Tasks closed today" value={performance?.tasksClosedToday ?? 0} />
                  <MetricRow
                    label="Avg. resolution time (hrs)"
                    value={(performance?.averageResolutionHours ?? 0).toFixed(1)}
                  />
                  <MetricRow
                    label="Escalations resolved (7d)"
                    value={performance?.escalationsResolvedThisWeek ?? 0}
                  />
                  <MetricRow
                    label="QA pass rate"
                    value={`${(performance?.qaPassRate ?? 0).toFixed(0)}%`}
                  />
                </div>
              </div>

              <div className="card lg:col-span-2" id="escalation-queue">
                <div className="card-header">
                  <h2 className="text-lg font-semibold text-ca-primary-900">Escalation Queue</h2>
                </div>
                <div className="card-body">
                  {loadingStates.summary ? (
                    <LoadingIndicator label="Loading escalations" />
                  ) : escalationQueue.length === 0 ? (
                    <p className="text-sm text-ca-primary-600">No tasks have escalated in the last 5 business days.</p>
                  ) : (
                    <div className="overflow-x-auto">
                      <table className="table">
                        <thead>
                          <tr>
                            <th>Task</th>
                            <th>Assigned To</th>
                            <th>Queue</th>
                            <th>Priority</th>
                            <th>Days Open</th>
                            <th></th>
                          </tr>
                        </thead>
                        <tbody>
                          {escalationQueue.map((task) => (
                            <tr key={task.taskId}>
                              <td>{task.title}</td>
                              <td>{task.assignedTo || '—'}</td>
                              <td>{task.workQueue || '—'}</td>
                              <td>
                                <span className={`badge ${badgeForPriority(task.priority)}`}>
                                  {task.priority}
                                </span>
                              </td>
                              <td>{task.daysOpen}</td>
                              <td>
                                <button
                                  className="btn btn-outline btn-sm"
                                  onClick={() => {
                                    setActiveTab(TAB_USERS);
                                    fetchUserDetail(task.assignedTo);
                                  }}
                                >
                                  Inspect
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                </div>
              </div>
            </section>
          </div>
        )}

        {activeTab === TAB_USERS && (
          <div className="space-y-6">
            <div className="card">
              <div className="card-header">
                <div className="flex justify-between items-center">
                  <h2 className="text-lg font-semibold text-ca-primary-900">SW – My Users</h2>
                  <button className="btn btn-outline btn-sm" onClick={fetchTeamUsers}>
                    Refresh
                  </button>
                </div>
              </div>
              <div className="card-body">
                {loadingStates.users ? (
                  <LoadingIndicator label="Loading team roster" />
                ) : !hasTeamUsers ? (
                  <p className="text-sm text-ca-primary-600">
                    No users found. Assign tasks to team members to populate this view.
                  </p>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="table">
                      <thead>
                        <tr>
                          <th>User</th>
                          <th>Open</th>
                          <th>Reserved</th>
                          <th>Deferred</th>
                          <th>Escalated</th>
                          <th>Last Action</th>
                          <th></th>
                        </tr>
                      </thead>
                      <tbody>
                        {teamUsers.map((member) => (
                          <tr key={member.username}>
                            <td className="font-semibold text-ca-primary-900">{member.username}</td>
                            <td>{member.openTasks}</td>
                            <td>{member.reservedTasks}</td>
                            <td>{member.deferredTasks}</td>
                            <td>{member.escalatedTasks}</td>
                            <td>
                              {member.lastActionAt
                                ? new Date(member.lastActionAt).toLocaleString()
                                : '—'}
                            </td>
                            <td>
                              <button
                                className="btn btn-primary btn-sm"
                                onClick={() => fetchUserDetail(member.username)}
                              >
                                Open Workspace
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            </div>

            <div className="card" id="user-workspace">
              <div className="card-header">
                <div className="flex justify-between items-center">
                  <div>
                    <h2 className="text-lg font-semibold text-ca-primary-900">SW – User Workspace</h2>
                    <p className="text-sm text-ca-primary-600">
                      Drill into a user’s assignments, escalate, redirect, or reserve tasks.
                    </p>
                  </div>
                  {selectedUser && (
                    <button className="btn btn-outline btn-sm" onClick={() => fetchUserDetail(selectedUser)}>
                      Refresh
                    </button>
                  )}
                </div>
              </div>
              <div className="card-body space-y-4">
                {loadingStates.userDetail ? (
                  <LoadingIndicator label="Loading user workspace" />
                ) : errors.user ? (
                  <p className="text-sm text-red-600">{errors.user}</p>
                ) : !userDetail ? (
                  <p className="text-sm text-ca-primary-600">
                    Select a user from the roster to view their workspace.
                  </p>
                ) : (
                  <>
                    <div className="grid md:grid-cols-4 gap-3">
                      <SummaryCard title="User" value={userDetail.username} />
                      <SummaryCard title="Open" value={userDetail.summary.openTasks} />
                      <SummaryCard title="Reserved" value={userDetail.summary.reservedTasks} />
                      <SummaryCard title="Escalated" value={userDetail.summary.escalatedTasks} />
                    </div>

                    <UserTaskSection
                      title="Open Tasks"
                      tasks={userDetail.openTasks}
                      onReserve={handleReserveTask}
                      onUnreserve={handleUnreserveTask}
                      onRedirect={handleRedirectTask}
                      onClose={handleCloseTask}
                    />
                    <UserTaskSection
                      title="Reserved Tasks"
                      tasks={userDetail.reservedTasks}
                      onReserve={handleReserveTask}
                      onUnreserve={handleUnreserveTask}
                      onRedirect={handleRedirectTask}
                      onClose={handleCloseTask}
                    />
                    <UserTaskSection
                      title="Deferred Tasks"
                      tasks={userDetail.deferredTasks}
                      onReserve={handleReserveTask}
                      onUnreserve={handleUnreserveTask}
                      onRedirect={handleRedirectTask}
                      onClose={handleCloseTask}
                    />
                    <UserTaskSection
                      title="Escalated Tasks"
                      tasks={userDetail.escalatedTasks}
                      onReserve={handleReserveTask}
                      onUnreserve={handleUnreserveTask}
                      onRedirect={handleRedirectTask}
                      onClose={handleCloseTask}
                    />
                  </>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const SummaryCard = ({ title, value }) => (
  <div className="card">
    <div className="card-body text-center py-6">
      <div className="text-3xl font-bold text-ca-primary-900">{value ?? '—'}</div>
      <p className="text-sm text-ca-primary-600 mt-1 uppercase tracking-wide">{title}</p>
    </div>
  </div>
);

const MetricRow = ({ label, value }) => (
  <div className="flex justify-between items-center border-b border-ca-secondary-200 pb-2">
    <span className="text-sm text-ca-primary-600">{label}</span>
    <span className="text-sm font-semibold text-ca-primary-900">{value}</span>
  </div>
);

const LoadingIndicator = ({ label }) => (
  <div className="flex items-center space-x-3 py-4 text-ca-primary-600">
    <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-ca-highlight-600"></div>
    <span className="text-sm">{label}…</span>
  </div>
);

const badgeForPriority = (priority) => {
  switch (priority) {
    case 'HIGH':
      return 'badge-danger';
    case 'LOW':
      return 'badge-success';
    default:
      return 'badge-warning';
  }
};

const UserTaskSection = ({ title, tasks, onReserve, onUnreserve, onRedirect, onClose }) => (
  <div className="space-y-3">
    <div className="flex items-center justify-between">
      <h3 className="text-md font-semibold text-ca-primary-900">{title}</h3>
      <span className="text-sm text-ca-primary-600">{tasks?.length ?? 0} task(s)</span>
    </div>
    {(!tasks || tasks.length === 0) ? (
      <p className="text-sm text-ca-primary-600">No tasks in this category.</p>
    ) : (
      <div className="overflow-x-auto">
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Priority</th>
              <th>Queue</th>
              <th>Due</th>
              <th>Reserved By</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.id}</td>
                <td>{task.title}</td>
                <td>
                  <span className={`badge ${badgeForPriority(task.priority)}`}>
                    {task.priority}
                  </span>
                </td>
                <td>{task.workQueue || '—'}</td>
                <td>{task.dueDate ? new Date(task.dueDate).toLocaleDateString() : '—'}</td>
                <td>{task.reservedBy || '—'}</td>
                <td>
                  <div className="flex gap-2">
                    <button className="btn btn-outline btn-xs" onClick={() => onReserve(task.id)}>
                      Reserve
                    </button>
                    <button className="btn btn-outline btn-xs" onClick={() => onUnreserve(task.id)}>
                      Release
                    </button>
                    <button className="btn btn-outline btn-xs" onClick={() => onRedirect(task.id)}>
                      Redirect
                    </button>
                    <button className="btn btn-outline btn-xs" onClick={() => onClose(task.id)}>
                      Close
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )}
  </div>
);

export default SupervisorDashboard;
